import React from 'react';
import './App.css';

function App() {
  const [todos, setTodos] = React.useState([]);
  const [todo, setTodo] = React.useState('');
  const [todoEdit, setTodoEdit] = React.useState('');
  const [todoEditText, setTodoEditText] = React.useState('')

  function handleSubmit(e) {
    e.preventDefault();

    const newTodo = {
      id: new Date().getTime(),
      text: todo,
      competed: false
    }

    // spread operator
    setTodos([...todos].concat(newTodo))
    setTodo('')
  }

  function toggleComplete(todoId) {
    const completedTodo = [...todos].map((todo) => {
      if (todo.id === todoId) {
        todo.competed = !todo.competed
      }
      return todo
    })
    setTodos(completedTodo)
  }

  function setEditingText(e) {

  }

  function editTodo(todoId) {
    // const 
  }

  function deleteTodo(todoId) {
    const removeTodo = [...todos].filter((todo) => todo.id !== todoId)
    setTodos(removeTodo)
  }


  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input type="text" onChange={(e) => setTodo(e.target.value)} value={todo} />
        <button type="submit">Add</button>
      </form>
      {
        todos.map((todo) => <div key={todo.id}>
          <input
            type="checkbox"
            onChange={() => toggleComplete(todo.id)}
            checked={todo.competed}
          />

          {
            todoEdit != todo.id ?
            alert()
              (
                <div>
                  <div>{todo.text}</div>
                  <button
                    onClick={() => editTodo(todo.id)}>
                    Edit
                  </button>
                </div>
              )
              :
              (
                <div>
                  <input
                    type="text"
                    onChange={(e) => setEditingText(e.target.value)}
                    value={todoEditText}
                  />
                  <button
                    onClick={() => editTodo(todo.id)}>
                    Update
                  </button>
                </div>
              )
          }
          <button
            onClick={() => deleteTodo(todo.id)}>
            Delete
          </button>
        </div>
        )
      }
    </div>
  );
}

export default App;
